/**
 * Indicates whether a number is even. Unlike `isDivisibleBy(2)`, `isEven` doesn't
 * not allow any tolerance for rounding errors in floating point numbers.
 *
 * @param data
 * @returns
 *
 * @example
 * ```
 * isEven(4);        // true
 * isEven(4.1);      // false
 * ```
 *
 * @category Math
 */
export declare function isEven(data: number): boolean;
