/**
 * Indicates whether a number is non-negative, i.e., positive or zero.
 *
 * @param data
 *
 * @example
 * ```
 * isNonNegative(2);      // true
 * isNonNegative(0);      // true
 * isNonNegative(-2);     // false
 * ```
 *
 * @category Math
 */
export declare function isNonNegative(data: number): boolean;
