/**
 * Returns false if `data` is `null` or `undefined`, otherwise true.
 *
 * @param data
 * @returns
 *
 * @example
 * ```
 * isNotNullish(null);      // true
 * isNotNullish(undefined); // true
 * isNotNullish(0);         // false
 * ```
 *
 * @category Logic
 */
export declare function isNotNullish(data: any): boolean;
