/**
 * Converts a number to a string in hexadecimal form, or returns an empty string if
 * the number is not an non-negative integer.
 *
 * @param data
 *
 * @example
 * ```
 * toHex(24);     // "18"
 * toHex(2.4);    // ""
 * ```
 *
 * @category Math
 * @category String
 */
export declare function toHex(data: number): string;
